						$.each(chars, function(i,s)
						{
							html = html.replace(new RegExp(i, 'g'), s);
						});

						// remove last br in FF
						if (this.utils.browser('mozilla'))
						{
							html = html.replace(/<br\s?\/?>$/gi, '');
						}

