						this.tidy.$div = $('<div />').append(html);

						// clean
						this.tidy.replaceTags();
						this.tidy.replaceStyles();
						this.tidy.removeTags();

						this.tidy.removeAttr();
						this.tidy.removeEmpty();
						this.tidy.removeParagraphsInLists();
